import React from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

export const Banner = ({ banners }) => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true
    };

    return (
        <Slider {...settings}>
            {banners.map((banner) => (
                <div key={banner.id_no} className='text-center '>
                    <img class="" src="https://uploads-ssl.webflow.com/60812e315b9a3608a16845ef/64cbc04ab1c51b395be3f8c9_Slide%20-%201%20(1).png" alt="Card image cap"></img>
                    <h2>{banner.text}</h2>
                    {banner.button && (
                        <button className='btn btn-outline-secondary btn-lg'>{banner.btn_name}</button>
                    )}
                </div>
            ))}
        </Slider>
    );
};
export default Banner;
