import React , {useState} from "react";
import SectionArray from "../data/SectionArray";
import cardsArray from "../data/cardsArray";
import Card from "./Card";

const Section = ({ SectionArray, cardsArray }) => {
    const [showMore, setShowMore] = useState(false);

    const handleShowMore = () => {
      setShowMore(true);
    };

    const filteredCards = cardsArray.filter(card => card.parent_sec_no === SectionArray.sec_no);
    const cardsToShow = showMore ? filteredCards : filteredCards.slice(0, 3);

    return (
      <div className={section.cN}>
        <h1>{section.title}</h1>
        {cardsToShow.map(card => (
          <Card key={card.id} card={card} />
        ))}
        {!showMore && <button onClick={handleShowMore}>See More</button>}
      </div>
    );
  };
  export default Section;
