import React, { useState } from 'react';

// Card component
const Card = ({ card }) => {
  return (
    <div className="card">
      <h2>{card.card_title}</h2>
      {card.data && <p>{card.data_type}: {card.data_value}</p>}
      {card.button && <button>{card.button_name}</button>}
      {card.link && <a href={card.redirect}>{card.link_name}</a>}
      {card.note && <p>{card.note}</p>}
    </div>
  );
};

// Section component
const Section = ({ section, cards }) => {
  const [showMore, setShowMore] = useState(false);

  const handleShowMore = () => {
    setShowMore(true);
  };

  const filteredCards = cards.filter(card => card.parent_sec_no === section.sec_no);
  const cardsToShow = showMore ? filteredCards : filteredCards.slice(0, 3);

  return (
    <div className={section.cN}>
      <h1>{section.title}</h1>
      {cardsToShow.map(card => (
        <Card key={card.id} card={card} />
      ))}
      {!showMore && <button onClick={handleShowMore}>See More</button>}
    </div>
  );
};

// Mock data
export default Section;
