import React from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

const banners = [
  {
    id: "banner1",
    id_no: 1,
    text: "This is the first banner area",
    button: true,
    btn_typ: "redirect",
    btn_name: "Banner",
    link: "/redirect1",
  },
  {
    id: "banner2",
    id_no: 2,
    text: "This is the second banner area",
    button: true,
    btn_typ: "redirect",
    btn_name: "Banner",
    link: "/redirect2",
  },
  {
    id: "banner3",
    id_no: 3,
    text: "This is the third banner area",
    button: false,
  },
];

function App() {
  const sliderRef = React.useRef();

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,

  };

  const handleClick = (banner) => {
    if (sliderRef.current) {
      sliderRef.current.slickNext();
    }
  }

  return (
    <div className="App ">
      <Slider ref={sliderRef} {...settings}>
        {banners.map((banner, index) => (
          <div key={banner.id} className='text-center '>
            <h2>{banner.text}</h2>
            {banner.button && (
              <button onClick={() => handleClick(banner)} className='btn btn-outline-secondary btn-lg'>
                {banner.btn_name}
              </button>
            )}
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default App;
